mapper.insert:
INSERT INTO board
SET title = #{title}
, contents = #{contents}
, writer_id = #{writerId}

mapper.select:
SELECT board_id AS boardID, title, contents, hits, created_at AS createdAt, updated_at AS updatedAt
FROM board
WHERE board_id = #{boardId}

mapper.update:
UPDATE board
SET updated_at = NOW()

    , title = #{title}


    , contents = #{contents}

WHERE writer_id = #{writerId}
